  useEffect(() => {
    const handleScroll = () => {
      const scrollPos = window.scrollY;

      // Update Navbar state
      setIsScrolled(scrollPos > 50);

      // Calculate Expertise Section Progress for smooth transition
      if (expertiseRef.current) {
        const rect = expertiseRef.current.getBoundingClientRect();
        const windowHeight = window.innerHeight;

        // Calculate how much of the section is in the viewport
        // 0 = just entered, 1 = fully centered/active
        const start = windowHeight;
        const end = -rect.height;
        const current = rect.top;

        // Progress based on center of screen
        const sectionCenter = rect.top + rect.height / 2;
        const screenCenter = windowHeight / 2;
        const distanceFromCenter = Math.abs(sectionCenter - screenCenter);
        const threshold = windowHeight * 0.8;

        let progress = 1 - (distanceFromCenter / threshold);
        progress = Math.max(0, Math.min(1, progress));
        setExpertiseProgress(progress);
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

